module("Mouse Navigation (All)",{setup:function(){this.input=$('<input type="text">').appendTo("#qunit-fixture").datetimepicker({format:"dd-mm-yyyy"}).focus();this.dp=this.input.data("datetimepicker");this.picker=this.dp.picker},teardown:function(){this.picker.remove()}});test("Clicking datetimepicker does not hide datetimepicker",function(){ok(this.picker.is(":visible"),"Picker is visible");this.picker.trigger("mousedown");ok(this.picker.is(":visible"),"Picker is still visible")});test("Clicking outside datetimepicker hides datetimepicker",function(){var a=$("<div />");$("body").append(a);ok(this.picker.is(":visible"),"Picker is visible");this.input.trigger("click");ok(this.picker.is(":visible"),"Picker is still visible");a.trigger("mousedown");ok(this.picker.is(":not(:visible)"),"Picker is hidden");a.remove()});